import React, { Component } from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";

import Signup from './Signup';

class Login extends Component {


    render() {
        return (
            <div className="container">
               <form id="reg-form">
                    <div className="username">
                        <label for="usrname">Username</label>
                        <input type="text" id="usrname" name="usrname" class="input-field"></input>
                    </div>
                    <div className="password">
                        <label for="psw">Password</label>
                        <input type="password" class="input-field" id="psw" name="psw" title="Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters"></input>
                    </div>
                    <button id="LoginSubmit" type="submit" value="Submit" class="submit-form" >Login</button>
                    <Router>
                        <Link to="/signup">
                            <button type="submit" value="Submit" class="submit-form" >Signup</button>
                        </Link>
                        <Switch>
                            <Route path="/signup">
                                <Signup />
                            </Route>
                        </Switch>
                    </Router>
               </form>
            </div>

        )
    }
}


export default Login;
