import React, { Component } from 'react';
import { connect } from 'react-redux';
import { loadPlaces} from '../store/places';
import { setLocation } from '../store/location';


import Button from './Button'


class Places extends Component {
    constructor(props) {
        super(props);
        this.itemUi = this.itemUi.bind(this);
    }

    componentDidMount() {
        const {loadPlaces, selectedLocation, cl} = this.props;
        console.log(selectedLocation);
    }

    itemUi(places) {
        return (
            <div key={places.id} className="product-item">
                <img alt={places.name} src={places.poster} />
                <div className="product-detail">
                    <div className="product-title">{places.name}</div>
                    <Button place={places} />
                </div>
            </div>
        )
    }

    render() {
        const {places, cl} = this.props;
        return (
            <div className="app-places">
                {places.map(this.itemUi)}
            </div>
        )
    }
}


const mapStateToProps = state => {
    return {
        places: state.places.list,
        cl: state.currentLocation.selectedLocation
    };
}

const mapDispatchToProps = dispatch => {
    return {
        loadPlaces: (id) => {
            dispatch(loadPlaces(id));
        }
    }
}


export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Places);
