import React, { Component } from 'react';
import { connect } from 'react-redux';
import { loadPlaces } from '../store/places';


class Places extends Component {
    constructor(props) {
        super(props);
        this.itemUi = this.itemUi.bind(this);
    }

    componentDidUpdate() {
        const {loadPlaces, selectedLocation} = this.props;
        loadPlaces(selectedLocation);
        console.log(selectedLocation);
    }

    itemUi(places) {
        return (
            <div key={places.id} className="product-item">
            <img alt={places.name} src={places.poster} />
            </div>
        )
    }

    render() {
        const {places} = this.props;
        return (
            <div className="app-places">
                {places.map(this.itemUi)}
            </div>
        )
    }
}


const mapStateToProps = state => {
    return {
        places: state.places.list
    };
}

const mapDispatchToProps = dispatch => {
    return {
        loadPlaces: (id) => {
            dispatch(loadPlaces(id));
        }
    }
}


export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Places);
